import React, {useState, useEffect, useCallback} from 'react';

import { useHistory } from 'react-router';
import { Menu, Button, Table, Tag, Space, Popconfirm, Typography, Form, Input, InputNumber, Switch } from 'antd';



export function UserManageComponent(props: any) {
  const history = useHistory();
  const {userList} = props || {};

  const [form] = Form.useForm();

  const changeStatus = (value: any)=> {
    console.log('ok hieu', value)
  }


  // const customSwitch = (value: any) => {
  //   console.log('value: ', value);
  //   return <Switch defaultChecked onChange={(e, record) => changeStatus(e, record)} />
  // }
  const columns = [
    {
      title: "ID", 
      dataIndex: "id",
      editable: false,
    },
    {
      title: "USER NAME",
      dataIndex: "username",
      editable: false,
    },
    {
      title: "FIRST NAME",
      dataIndex: "firstName",
      editable: false,
    },
    {
      title: "LAST NAME",
      dataIndex: "lastName",
      editable: false,
    },
    {
      title: "EMAIL",
      dataIndex: "email",
      editable: false,
    },
    {
      title: "STATUS",
      dataIndex: "status",
      editable: true,
      render: (_: any, record: any) => {
        console.log('ss', record); 

        return <Switch defaultChecked={false} onChange={() => changeStatus(record)} />
      }
    },
    {
      title: "CREATED DATE",
      dataIndex: "createdDate",
      editable: false,
    }
  ]

  const mergedColumns = columns.map(col => {
    if (!col.editable) {
      return col;
    }
    return {
      ...col,
      onCell: (record: any) => ({
        record,
        //inputType: col.dataIndex === 'age' ? 'number' : 'text',
        dataIndex: col.dataIndex,
        title: col.title,
      }),
    };
  });

  return (
    <Form form={form} component={false}>
      <Table 
        columns={mergedColumns}
        loading={userList.loading || !!userList.error} 
        dataSource={userList.data?.items}
      />      
    </Form>

  )
}
