import React, {useState, useEffect, useCallback} from 'react';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  TeamOutlined,
  ReadFilled,
  UserOutlined
} from '@ant-design/icons';
import { RouteComponentProps, useHistory } from 'react-router';
import { useQuery } from "../../shared/hooks/useQuery.hook";
import { Menu, Button, Form, Input, InputNumber } from 'antd';

import { UserService } from '../user/UserService'
import { IContentSearchRequest } from '../user/types/SearchUserRequest';
import { UserRole } from '../../shared/enums/UserRole';
import {UserManageComponent} from './UserManageComponent';

const LINK = {
  ["/admin/students"]: UserRole.NORMAL,
  ["/admin/teachers"]: UserRole.TEACHER,
  ["/admin/admin"]: UserRole.ADMIN,
  ["/admin/courses"]: 5,
}

export function AdminPage(props: RouteComponentProps) {
  const history = useHistory();
  const [collapsed, setCollapsed] = useState(false);

  const [req, setReq] = useState({
    page: 1,
    pageSize: 10,
    role: history.location.pathname.includes('') ? UserRole.NORMAL : (history.location.pathname.includes('teachers') ? UserRole.TEACHER : UserRole.ADMIN)
  })

  const userList = useQuery(
    useCallback(()=> UserService.search(req), [req])
  );



  return (
    <div className="flex space-x-4">
      <div className="">
        <Button type="primary" onClick={()=>setCollapsed(!collapsed)} style={{ marginBottom: 16 }}>
            {React.createElement(collapsed ? MenuUnfoldOutlined : MenuFoldOutlined)}
          </Button>
          <Menu
            defaultSelectedKeys={[props.location.pathname]}
            defaultOpenKeys={['admin/students']}
            mode="inline"
            theme="dark"
            onSelect={e => { const key = e.key;history.push(e.key as any); setReq({...req, role: LINK["/admin/teacher"]} )}}
            inlineCollapsed={collapsed}
          >
            <Menu.Item key="/admin/students" icon={<TeamOutlined />}>
              Quản lý học viên
            </Menu.Item>
            <Menu.Item key="/admin/teachers" icon={<UserOutlined />}>
              Quản lý giảng viên
            </Menu.Item>
            <Menu.Item key="/admin/courses" icon={<ReadFilled />}>
              Quản lý khóa học
            </Menu.Item>
          </Menu>
        </div>
      <div className="w-11/12">
        <UserManageComponent
          userList={userList}
        />
      </div>
    </div>
  )
}
