import React, {useState, useEffect, useCallback} from 'react';

import { useHistory } from 'react-router';
import { Menu, Button, Table, Tag, Space, Popconfirm, Typography, Form, Input, InputNumber, Switch } from 'antd';



export function UserManageComponent(props: any) {
  const history = useHistory();
  const {userList} = props || {};
  const changeStatus = (e: any,value: any)=> {
    console.log('ok hieu',e, value)
  }


  const customSwitch = (value: any) => {
    console.log('value: ', value);
    return <Switch defaultChecked onChange={(e, record) => changeStatus(e, record)} />
  }
  const columns = [
    {
      title: "ID", 
      dataIndex: "id",
      editable: false,
    },
    {
      title: "USER NAME",
      dataIndex: "username",
      editable: true,
    },
    {
      title: "FIRST NAME",
      dataIndex: "firstName",
      editable: true,
    },
    {
      title: "LAST NAME",
      dataIndex: "lastName",
      editable: true,
    },
    {
      title: "EMAIL",
      dataIndex: "email",
      editable: true,
    },
    {
      title: "STATUS",
      dataIndex: "status",
      editable: true,
      render: (_: any, record: any) => customSwitch(record)
    },
    {
      title: "CREATED DATE",
      dataIndex: "createdDate",
      editable: false,
    }
  ]

  return (
    <Table 
      columns={columns}
      loading={userList.loading || !!userList.error} 
      dataSource={userList.data?.items}
    />
  )
}
